script:
.svg-canvas{
	background-color: #fff;
}
path {
	  -webkit-transition: all 2.5s ease-in-out, fill 1s ease-in;
	  -webkit-transition-delay: 0s, 2.5s;
	  -moz-transition: all 2.5s ease-in-out, fill 1s ease-in 2.5s;
	  -o-transition: all 2.5s ease-in-out, fill 1s ease-in 2.5s;
	  transition: all 2.5s ease-in-out, fill 1s ease-in 1.5s;
	fill: transparent;
	stroke-width: 2;
	stroke: #fff;
	stroke-dasharray: 252px, 252px;
	stroke-dashoffset: 252px;
}

body {
  background-color: #BF2F2F;
  text-align: center;
  padding-top: 10%;
}
path.fillIt {
	stroke-dashoffset: 0;
	stroke: #36d;
}
